package sec02.exam02_vector;

import java.util.List;
import java.util.Vector;

//게시판 만들때 이런방식
public class VectorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		List<board> list = new Vector<board>();
		
		list.add(new board("제목1","내용1","글쓴이1"));//0
		list.add(new board("제목2","내용2","글쓴이2"));//1
		list.add(new board("제목3","내용3","글쓴이3"));//2->삭제
		list.add(new board("제목4","내용4","글쓴이4"));//3>삭제
		list.add(new board("제목5","내용5","글쓴이5"));//4->2
		
		list.remove(2);
		list.remove(3);
		
		for(int i=0; i<list.size(); i++) {
			board B = list.get(i);
			System.out.println(B.subject+"\t"+B.content+"\t"+B.writer+" "+i);
		}
	}

}
